import React from 'react'
const [value, setValue] = useState(0);

function ItemDescription({name, description}) {

    function increment() {
        setValue(value+1);
    }
    function decrement() {
        setValue(value+1);
    }

    return (
        <div>
            <h2>{name}</h2>
            {/* <p>{description}</p> */}
            <p>{value}</p>
            <button onClick={decrement} >-</button>
            <button onClick={increment} >+</button>
        </div>
    )
}

export default ItemDescription
