import React, { useEffect } from "react";

function JokeGenerator() {
  useEffect(() => {
    const fetchJoke = async () =>
      await fetch(
          `http://api.icndb.com/joke/ramdom`        // for first name and lastname in joke we can use ..random?firstName=Ritik&lastName=Kumar
          ).then(res = r)
  }, []);

  return (
    <div className="app">
      <center>
        <h1>The Joke Generator</h1>

        <button>Generate Joke</button>
      </center>
    </div>
  );
}

export default JokeGenerator;
